import React, {useState, useEffect} from 'react'



function Card(props){
        const [names, setName]=useState(['Cassidy','Johnatan','Carol','Muriel','Arthur','Tom','Jolyne','Dio','Giorno','Brandon'])
        const [id,setId] = useState(0)
        if(id===0){
        setId(props.id)
        }




    return (
      <div id={id} key={id+1} className='cardContainer' onClick={()=>props.getData(id)}>
          <div className='imageContainer'>
              <img src={`./imgs/cat${id}.jpg`} alt="Cat" />
          </div>
          <div className='cardName'>{`${names[id]}`}</div>
      </div>
  )}


export default Card;
