import React from 'react'



class Card extends React.Component{
    constructor(props){
      super(props)
      this.state = {
        name: 'cat',
      }
      this.handleClick = this.handleClick.bind(this)
      this.changeName = this.changeName.bind(this)
    }

    handleClick(){
        this.changeName()
    this.props.checkGame();
    this.props.parentCallback(this.props.id);
    }
    changeName(){
        let nr = this.props.id;
        // eslint-disable-next-line default-case
        switch (nr) {
            case 0: 
                this.setState({name: 'Cassidy'});
                break;
            case 1:
                this.setState({name: 'Johnatan'})
                break;
            case 2:
                this.setState({name: 'Carol'})
                break;
            case 3:
                this.setState({name: 'Muriel'})
                break;
            case 4: 
                this.setState({name: 'Arthur'})
                break;
            case 5: 
                this.setState({name: 'Tom'})
                break;
            case 6:
                this.setState({name: 'Jolyne'})
                break;
            case 7:
                this.setState({name: 'Dio'})
                break;
            case 8:
                this.setState({name: 'Giorno'})
                break;
            case 9:
                this.setState({name: 'Brandon'})
                break;

        }
    }
    render(){
    return (
      <div id={this.props.id} key={this.props.id+1} className='cardContainer' onClick={this.handleClick}>
          <div className='imageContainer'>
              <img src={`./imgs/cat${this.props.id}.jpg`} alt="Cat" />
          </div>
          <div className='cardName'>{`${this.state.name} ${this.props.id}`}</div>
      </div>
  )}
  }

export default Card;
