  array.forEach((item)=>{
    uis.push(render)
  })



  setCardsUI(uis);
  console.log(cards, uis)
  }
  const checkValue = () =>{
    console.log(cardId)
  }





 return(
   <div className='content'>
   <button type='button' onClick={checkValue}>Check</button>
  <button type='button' onClick={getCards}>Shuffle cards</button>
   <Scoreboard />
   <div className='cardsContainer'>

   {cardsUI}
   </div>

   </div>
 )
}

