import React, {useState, useEffect} from 'react'



class Card extends React.Component{
    constructor(props){
        super(props)
        this.state={
            names:['Cassidy','Johnatan','Carol','Muriel','Arthur','Tom','Jolyne','Dio','Giorno','Brandon']
        }


    }
    render(){
    return (
      <div id={props.id} key={props.id+1} className='cardContainer' onClick={console.log(props.id)}>
          <div className='imageContainer'>
              <img src={`./imgs/cat${props.id}.jpg`} alt="Cat" />
          </div>
          <div className='cardName'>{`${names[props.id]}`}</div>
      </div>
  )}
    }

export default Card;
