import React, {useState, useEffect} from 'react'



function Card(props){
        const [names, setName]=useState(['Cassidy','Johnatan','Carol','Muriel','Arthur','Tom','Jolyne','Dio','Giorno','Brandon'])



    const handleClick = ()=>{
        props.checkGame();
        props.getData(props.id)

    }
    return (
      <div id={props.id} key={props.id+1} className='cardContainer' onClick={handleClick}>
          <div className='imageContainer'>
              <img src={`./imgs/cat${props.id}.jpg`} alt="Cat" />
          </div>
          <div className='cardName'>{`${names[props.id]}`}</div>
      </div>
  )}


export default Card;
