  //       if(clone[j]===val){
  //         console.log(`${val} is repeated`)
  //       }
  //     }

  //   }
  // }




  render(){
 return(
   <div className='content'>
   <button type='button' onClick={this.checkValue}>Check</button>
  <button type='button' onClick={this.getCards}>Shuffle cards</button>
   <Scoreboard />
   <div className='cardsContainer'>
    {this.state.cardsUI}
   </div>

   </div>
 )}
}

class Card extends React.Component{
  constructor(props){
    super(props)
    this.state = {
      name: 'cat',
      cardId: this.props.id,
    }
    this.handleClick = this.handleClick.bind(this)
  }

  handleClick(){
    this.props.parentCallback(this.state.cardId);
    //this.setState({cardId: this.props.id})
    console.log(this.state.cardId)
  }
  render(){
  return (
    <div id={this.state.cardId} className='cardContainer' onClick={this.handleClick}>
        <div className='imageContainer'>
            <img src={`./imgs/cat${this.state.cardId}.jpg`} alt="Cat" />
        </div>
        <div className='cardName'>{`${this.state.name} ${this.state.cardId}`}</div>
    </div>
)}
}
export {Card};


export default App;
