import React from 'react'



class Card extends React.Component{
    constructor(props){
      super(props)
      this.state = {
        name: 'cat',
      }
      this.handleClick = this.handleClick.bind(this)
    }

    handleClick(){
      this.props.parentCallback(this.props.id);
    }
    render(){
    return (
      <div id={this.props.id} className='cardContainer' onClick={this.handleClick}>
          <div className='imageContainer'>
              <img src={`./imgs/cat${this.props.id}.jpg`} alt="Cat" />
          </div>
          <div className='cardName'>{`${this.state.name} ${this.props.id}`}</div>
      </div>
  )}
  }

export default Card;
